                    } else if (postsStyle === 'testimonial') {
                        var indicatorHTML = '';
                        for (var l = 0; l < postsToDisplay; l++) {
                            var active = l === 0 ? ' active ' : '';
                            indicatorHTML += '<li data-target="#btemplates-posts-slider" data-slide-to="' + l + '" class="' + active + '"></li>';
