                function btemplatesGetComments(el, commentsToDisplay) {
                    commentsToDisplay = parseInt(commentsToDisplay);
                    var request = new XMLHttpRequest();
                    request.open('GET', blogurl + '/feeds/comments/summary?alt=json&max-results=' + commentsToDisplay, true);

                    request.onload = function () {
                        if (request.status >= 200 && request.status < 400) {
                            // Success!
                            var data = JSON.parse(request.responseText);
                            var code = btemplatesProcessComments(data, commentsToDisplay);
                            el.innerHTML = code;
                            return code;
                        } else {
                            // We reached our target server, but it returned an error

                        }
                    };

                    request.onerror = function () {
                        // There was a connection error of some sort
                    };

                    request.send();
                }

                function btemplatesProcessComments(json, commentsToDisplay) {
                    if (json.feed.entry === undefined)
                        return 'No comments found';
                    var commentHTML = '';
                    var months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

                    for (var i = 0; i < commentsToDisplay; i++) {
                        var comment = commentDateTime = commentDate = commentYear = commentMonth = commentMonthName =
                                commentDay = commentUrl = commentContent = commentSummary = commentAuthor =
                                        commentAuthorAvatar = postTitle = '';
                        comment = json.feed.entry[i];
                        if (comment === undefined)
                            continue;
                        commentDateTime = comment.published.$t;
                        commentDate = commentDateTime.substring(0, 10);
                        commentYear = commentDate.substring(0, 4);
                        commentMonth = commentDate.substring(5, 7);
                        commentMonthName = months[parseInt(commentMonth) - 1];
                        commentDay = commentDate.substring(8, 10);
                        commentAuthor = comment.author[0].name.$t;
                        for (var j = 0; j < comment.link.length; j++) {
                            if (comment.link[j].rel === 'alternate') {
                                commentUrl = comment.link[j].href;
                                break;
                            }
                        }
                        commentContent = commentSummary = comment.summary.$t;
                        commentAuthorAvatar = comment.author[0].gd$image.src;
                        if (commentAuthorAvatar.indexOf('blogblog.com/img/blank.gif') !== -1 ) {
                            commentAuthorAvatar = 'https://3.bp.blogspot.com/-qnvargRhquo/XSJ9VCaxZ4I/AAAAAAAABNU/f5c-lN4M9dIl3Wr87zT_guszgZY9RDe6gCLcBGAs/s100/user.png';
                        }
                        postTitle = comment.title.$t;

                        commentHTML += '<div class="btemplates-comment">' +
                                '<div class="bt-comment-avatar">' +
                                '<a href="' + commentUrl + '">' +
                                '<img class="profile-img rounded-circle border border-secondary" src="' + commentAuthorAvatar + '" alt="' + commentAuthor + '"/>' +
                                '</a>' +
                                '</div>' +
                                '<div class="bt-comment-author">' +
                                '<a href="' + commentUrl + '">' +
                                commentAuthor +
                                '</a>:' +
                                '<div class="bt-comment-content">' +
                                postTitle +
                                '</div>' +
                                '<div class="bt-comment-date">' +
                                '<a href="' + commentUrl + '">' +
                                commentDate +
                                '</a>' +
                                '</div>' +
                                '</div>';
                    }
                    return commentHTML;
                }


                linkListUl = $j('.navbar .section ul').each(function(){ $j(this).addClass('navbar-nav') });
                $j('.navbar .section li').each(function(i){
                    var navItemText = $j(this).children('a').text();
                    $j(this).addClass('nav-item align-self-lg-center');
                    $j(this).children('a').addClass('nav-link');
                    var re = /^-([^-]+)/i;
                    var resub = /^--([^-]+)/i;
                    var retag = /^(tag:[^-]+)-?(\d+)?-?(true|false)?/i;
                    if (re.exec(navItemText) && ajaxSupport) {
                        var previousNavItem = $j(this).prev();
                        if(previousNavItem.length){
                            var navItemLink = $j(this).children('a').attr('href');
                            var navItemNewText = navItemText.replace(/^-/i, '');
                            var nextNavItem = $j(this).next();
                            var nextNavItemText = nextNavItem.text();
                            var previousNavItemLink = previousNavItem.children('a').attr('href');
                            var previousNavItemText = previousNavItem.children('a').text();
                            var newLink = subitems = '';
                            while( resub.exec($j(this).next().text()) ) {
                                subitems += '<a class="dropdown-item" href="' + $j(this).next().children('a').attr('href') + '">' +
                                 '' + $j(this).next().text().replace('--', '') + '</a>';
                                $j(this).next().remove();
                            }
                            $j(this).remove();

                            if( resub.exec(nextNavItemText) ) {
                                newLink = '<div class="dropdown-submenu">' +
                                 '<a id="sub-dropdown-'+i+'" role="button" class="dropdown-item dropdown-toggle" ' +
                                  'data-toggle="dropdown" aria-haspopup="true" aria-expanded="false" ' +
                                   'href="'+navItemLink+'">'+navItemNewText+'</a>' +
                                    '<div class="dropdown-menu" aria-labelledby="sub-dropdown-'+i+'">' +
                                     subitems +
                                     '</div>' +
                                      '</div>';
                            } else {
                                newLink = '<a class="dropdown-item" href="' + navItemLink + '">' + navItemNewText + '</a>';
                            }
                            if (previousNavItem.hasClass('dropdown')) {
                                var dropdownList = previousNavItem.find('.dropdown-menu').first();
                                dropdownList.append(newLink);
                            } else {
                                previousNavItem.className += ' ' + 'dropdown';
                                previousNavItem.addClass('dropdown');
                                previousNavItem.html('<a class="nav-link dropdown-toggle" href="' + previousNavItemLink +
                                        '" role="button" data-toggle="dropdown" id="dropdown-' + i + '" aria-haspopup="true" aria-expanded="false">' +
                                        previousNavItemText +
                                        '</a>' +
                                        '<div class="dropdown-menu" aria-labelledby="dropdown-' + i + '">' +
                                        newLink +
                                        '</div>');
                            }
                        }
                    } else if (retag.exec(navItemText) && ajaxSupport) {
                        var dropdownSettings = navItemText.match(retag);
                        var postsType = dropdownSettings[1];
                        var postsToDisplay = parseInt(dropdownSettings[2]);
                        var postsThumbnailSize = dropdownSettings[3] === 'true' ? 200 : 0;
                        $j(this).addClass('dropdown');
                        $j(this).children('a').html(postsType.replace(/tag:|-\d/, ''));
                        btemplatesJqueryGetPosts($j(this), postsToDisplay, postsType, postsThumbnailSize, 'menulist');
                    }
                });
                $j('#topMenu').removeClass('d-none');

                $j(function() {
                    $j("div.dropdown-menu [data-toggle='dropdown']").on("click", function(event) {
                        event.preventDefault();
                        event.stopPropagation();
                        $j(this).closest('.dropdown-menu.show').find('.dropdown-menu').removeClass('show');

                        $j(this).siblings().toggleClass("show");


                        if (!$j(this).next().hasClass('show')) {
                            $j(this).parents('.dropdown-menu').first().find('.show').removeClass("show");
                        }
                        $j(this).parents('li.nav-item.dropdown.show').on('hidden.bs.dropdown', function(e) {
                            $j('.dropdown-submenu .show').removeClass("show");
                        });

                    });
                });

